<?php class User_Model_DbTable_User extends Zend_Db_Table_Abstract{	public function __construct(){		$options = array( 			'db' 			=> Zend_Controller_Front::getInstance()							->getParam('bootstrap')							->getResource('db'),			'name' 			=> 'user',			'primary' 		=> 'id',			'metadataCache' => Zend_Controller_Front::getInstance()								->getParam('bootstrap')								->getResource('cachemanager')								->getCache('metadata')		);		parent::__construct( $options );	}}